                            zhu_win: 0,
                            zhu_lose: 0,
                            zhu_tie: 0,
                            zhu_side_win: 0,
                            zhu_side_lose: 0,
                            zhu_against: 0,
                            zhong: 0,
                            zhong_win: 0,
                            zhong_lose: 0,
                            zhong_tie: 0,
                            zhong_death: 0,
                            zhong_side_win: 0,
                            zhong_side_lose: 0,
                            zhong_against: 0,
                            fan: 0,
                            fan_win: 0,
                            fan_lose: 0,
                            fan_tie: 0,
