            lib.element.card.canBeUsedToRespond = function(card, player, trigger, forced) {
                if (!player) player = get.owner(this);
                if (!player) player = _status.event.player;
                if (!trigger) trigger = _status.event.getParent('useCard', true);
                if (!trigger) trigger = {
                    excluded: [],
                    directHit: [],
                    nowuxie: false,
                };
                if (!card) card = trigger.card;
                if (!card) return false;
                if (trigger.excluded.contains(player)) return true;
                if (trigger.directHit.contains(player)) return false;
                if (!lib.filter.cardEnabled(this, player, trigger)) return false;
                if (get.type(card, null, trigger.player) == 'trick') {
                    if (get.name(this, player) == 'wuxie') {
                        return !trigger.nowuxie;
                    };
                };
                if (get.tag(card, 'respondSha')) {
                    if (['sha', 'hufu', 'yuchanqian'].contains(get.name(this, player))) return true;
                };
                if (get.tag(card, 'respondShan')) {
                    if (['shan', 'hufu'].contains(get.name(this, player))) return true;
                };
                if (get.tag(card, 'damage') && forced !== true) {
                    if (get.tag(this, 'save')) return true;
                };
                return false;
            };
            lib.element.player.canRespondTo = function(trigger, card, step, extra) {
                var player = this;
                if (!trigger) {
                    if (extra == 'forced' || get.itemtype(extra) == player) {
                        trigger = {
                            name: 'useCard',
                            player: _status.event.player,
                            card: card,
                            target: player,
                            targets: [player],
                            excluded: [],
                            directHit: [],
                            customArgs: {
                                default: {}
                            },
                            step: step,
                        };
                        if (get.itemtype(extra) == player) trigger.player = extra;
                    } else {
                        trigger = _status.event;
                    };
                };
                if (trigger.name != 'useCard') trigger = trigger.getParent('useCard', true);
                if (!trigger) return false;
                if (trigger.excluded.contains(player)) return true;
                if (trigger.directHit.contains(player)) return false;

                if (!card) card = trigger.card;
                if (!card) card = _status.event.card;
                if (!card) return false;

                if (trigger.player.hasSkillTag('directHit_ai', true, {
                        target: player,
                        card: card,
                        step: step,
                    }, true)) return false;

                var skills = player.getSkills(true).concat(lib.skill.global);
                game.expandSkills(skills);

                var id = player.playerid;
                var map = trigger.customArgs;
                var need = 1;
                var have = 0;

                if (get.type(card, null, trigger.player) == 'trick' && !trigger.nowuxie) {
                    if (player.countCards('hs', function(cardx) {
                            return lib.filter.cardEnabled(cardx, player, 'forceEnable') && get.name(cardx, player) == 'wuxie';
                        }) > 0) return true;
                    for (var i = 0; i < skills.length; i++) {
                        var info = get.info(skills[i]);
                        if (info.viewAs && typeof info.viewAs != 'function' && info.viewAs.name == 'wuxie') {
                            if (!info.viewAsFilter || info.viewAsFilter(player)) {
                                return true;
                            }
                        } else {
                            var hiddenCard = get.info(skills[i]).hiddenCard;
                            if (typeof hiddenCard == 'function' && hiddenCard(player, 'wuxie')) {
                                return true;
                            };
                        };
                    };
                };
                if (get.tag(card, 'respondSha')) {
                    if (map[id] && typeof map[id].shaReq == 'number') need = map[id].shaReq;
                    have = player.countCards('hs', function(cardx) {
                        return lib.filter.cardEnabled(cardx, player, 'forceEnable') && ['sha', 'hufu', 'yuchanqian'].contains(get.name(cardx, player));
                    });
                    if (player.hasSkillTag('respondSha', true, 'respond', true)) {
                        have++;
                    };
                    if (have >= need) {
                        return true;
                    };
                };
                if (get.tag(card, 'respondShan')) {
                    if (map[id] && typeof map[id].shanRequired == 'number') need = map[id].shanRequired;
                    have = player.countCards('hs', function(cardx) {
                        return lib.filter.cardEnabled(cardx, player, 'forceEnable') && ['shan', 'hufu'].contains(get.name(cardx, player));
                    });
                    if (player.hasSkillTag('respondShan', true, null, true)) have++;
                    if (have >= need) {
                        return true;
                    };
                };
                return false;
            };

